mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT m.memberName, m.memberPhone, r.rentPlace, b.bikeType, b.bikeCode, b.bikeState, e.eidx, e.errorDay, e.errorContent FROM rent1234 r JOIN member1234 m ON m.midx = r.midx JOIN bike1234 b ON b.bkidx = r.bkidx JOIN error1234 e ON e.ridx = r.ridx WHERE 1=1
ORDER BY e.errorDay DESC ) A WHERE ROWNUM <= #{page} * #{perPageNum}
) B WHERE B.rnum >= (#{page} - 1) * #{perPageNum} + 1

SELECT m.memberName, m.memberPhone, r.rentPlace, b.bikeType, b.bikeCode, b.bikeState, e.eidx, e.errorDay, e.errorContent FROM rent1234 r JOIN member1234 m ON m.midx = r.midx JOIN bike1234 b ON b.bkidx = r.bkidx JOIN error1234 e ON e.ridx = r.ridx WHERE 1=1
ORDER BY e.errorDay DESC ) A WHERE ROWNUM <= #{page} * #{perPageNum}
) B WHERE B.rnum >= (#{page} - 1) * #{perPageNum} + 1

mapper.update:
 	UPDATE bike1234
 	SET bikeState = #{bikeState}
	WHERE bikeId = #{bikeId}
